#include<stdio.h>
int main()
{
	int a=20,b=30;
	printf("incrementing value of a =%d\n",++a);
	printf("decrementing value of b =%d\n",--b);
	printf("decrementing value of a =%d\n",a--);
	printf("value of a=%d\n",a);
	printf("incrementing value of b=%d\n",b++);
    printf("decrementing value of b=%d\n",b++);
	printf("value of b=%d\n",b);
	return 0;
}